    `,
  },
  // Repita o processo para os dias 02 a 40.
};

function App() {
  const [selectedDay, setSelectedDay] = useState(null);

  const handleButtonClick = (day) => {
    // Converte o número do dia para uma string com dois dígitos
    const dayKey = String(day).padStart(2, '0');  // "1" se tornará "01"
    setSelectedDay(dayKey);
  };


  const handleBackButton = () => {
    setSelectedDay(null);
  };

  return (
    <div className="app-container">
      <h1 className="title">Quaresma com a Bíblia</h1>

      {selectedDay === null ? (
        <div>
          {[...Array(40)].map((_, index) => {
            const day = index + 1;
            return (
              <button
                key={day}
                onClick={() => handleButtonClick(day)}
                className="button"
              >
                Dia {day}
              </button>
            );
          })}
        </div>
      ) : (
        // Verifica se o conteúdo existe para o dia selecionado
        reflections[selectedDay] ? (
          <div className="text-container">
            <h2>{reflections[selectedDay].title}</h2>
            <h3>{reflections[selectedDay].subtitle}</h3>
            <p>{reflections[selectedDay].content}</p>
            <button className="back-button" onClick={handleBackButton}>Voltar</button>
          </div>
        ) : (
          <div>
            <p>Dia não encontrado.</p>
            <button className="back-button" onClick={handleBackButton}>Voltar para a página principal</button>
          </div>
        )
      )}

      <footer className="footer">
        Assista à leitura e comentário deste estúdio bíblico feito por Ricardo Braz em: <a href="https://youtu.be/595S0ffMmNo" target="_blank" rel="noopener noreferrer">https://youtu.be/595S0ffMmNo</a>
      </footer>
    </div>
  );
}

export default App;
