  color: #5d1e8c; /* Roxo escuro */
  font-family: Arial, sans-serif;
  text-align: center;
  padding: 20px;
  min-height: 100vh;
`;

const Title = styled.h1`
  font-size: 3rem;
  color: #5d1e8c;
`;

const Button = styled.button`
  background-color: ${(props) => props.bgColor || '#7f42b5'}; /* Cor do botão */
  color: white;
  border: none;
  border-radius: 5px;
  margin: 5px;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 1.2rem;

  &:hover {
    background-color: #9b65d2;
  }
`;

