  background-color: #f0f0f0;
  padding: 20px;
  margin-top: 20px;
  border-radius: 5px;
`;

const BackButton = styled(Button)`
  margin-top: 20px;
`;

const Footer = styled.footer`
  margin-top: 40px;
  color: gray;
  font-size: 0.9rem;
`;

