    `,
  },
  // Repita o processo para os dias 02 a 40.
};

function App() {
  const [selectedDay, setSelectedDay] = useState(null);

  const handleButtonClick = (day) => {
    setSelectedDay(day);
  };

  const handleBackButton = () => {
    setSelectedDay(null);
  };

  return (
    <AppContainer>
      <Title>Quaresma com a Bíblia</Title>

      {selectedDay === null ? (
        <div>
          {[...Array(40)].map((_, index) => {
            const day = index + 1;
            return (
              <Button
                key={day}
                onClick={() => handleButtonClick(day)}
                bgColor={`#7f42b5`}
              >
                Dia {day}
              </Button>
            );
          })}
        </div>
      ) : (
        <TextContainer>
          <h2>{reflections[selectedDay].title}</h2>
          <h3>{reflections[selectedDay].subtitle}</h3>
          <p>{reflections[selectedDay].content}</p>
          <BackButton onClick={handleBackButton}>Voltar</BackButton>
        </TextContainer>
      )}

      <Footer>
        Assista à leitura e comentário deste estúdio bíblico feito por Ricardo Braz em: <a href="https://youtu.be/595S0ffMmNo" target="_blank" rel="noopener noreferrer">https://www.youtube.com/@bibliaparacatolicos</a>
      </Footer>
    </AppContainer>
  );
}

export default App;
